import React, { useState, useEffect } from 'react';

function ReviewsPage() {
  const [newReview, setNewReview] = useState({
    course_id: '',
    review_text: '',
    ratings: 0,
    professor_id: '',
  });
  const [profs, setProfs] = useState([]);
  const [courses, setCourses] = useState([]);
  const [filteredCourses, setFilteredCourses] = useState([]);

  // Fetch professors and courses on mount
  useEffect(() => {
    const fetchProfs = async () => {
      try {
        const response = await fetch('http://localhost/path-to-your-api/reviews.php?resource=professors');
        const data = await response.json();
        setProfs(data);
      } catch (error) {
        console.error('Failed to fetch professors:', error);
      }
    };

    const fetchCourses = async () => {
      try {
        const response = await fetch('http://localhost/path-to-your-api/reviews.php?resource=courses');
        const data = await response.json();
        setCourses(data);
      } catch (error) {
        console.error('Failed to fetch courses:', error);
      }
    };

    fetchProfs();
    fetchCourses();
  }, []);

  // Filter courses when a professor is selected
  useEffect(() => {
    if (newReview.professor_id) {
      const filtered = courses.filter(
        (course) => course.professor_id === parseInt(newReview.professor_id)
      );
      setFilteredCourses(filtered);
    } else {
      setFilteredCourses([]);
    }
  }, [newReview.professor_id, courses]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewReview((prevState) => ({ ...prevState, [name]: value }));
  };

  const handleRatingChange = (rating) => {
    setNewReview((prevState) => ({ ...prevState, ratings: rating }));
  };

  const handleCreateReview = async () => {
    try {
      const response = await fetch('http://localhost/path-to-your-api/reviews.php', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(newReview),
      });
      const result = await response.json();

      if (response.ok) {
        alert('Review submitted successfully!');
        setNewReview({ course_id: '', review_text: '', ratings: 0, professor_id: '' });
      } else {
        console.error('Failed to submit review:', result.message);
      }
    } catch (error) {
      console.error('Failed to create review:', error);
    }
  };

  return (
    <div>
      <h1>Leave a Review</h1>
      <select
        name="professor_id"
        value={newReview.professor_id}
        onChange={handleInputChange}
      >
        <option value="">Select Professor</option>
        {profs.map((prof) => (
          <option key={prof.professor_id} value={prof.professor_id}>
            {prof.name}
          </option>
        ))}
      </select>

      <select
        name="course_id"
        value={newReview.course_id}
        onChange={handleInputChange}
        disabled={!newReview.professor_id}
      >
        <option value="">Select Course</option>
        {filteredCourses.map((course) => (
          <option key={course.course_id} value={course.course_id}>
            {course.course_name}
          </option>
        ))}
      </select>

      <textarea
        name="review_text"
        value={newReview.review_text}
        onChange={handleInputChange}
        placeholder="Write your review here..."
        rows="5"
        style={{ width: '100%' }}
      />

      <div>
        <p>Rating:</p>
        {[1, 2, 3, 4, 5].map((star) => (
          <span
            key={star}
            onClick={() => handleRatingChange(star)}
            style={{
              cursor: 'pointer',
              fontSize: '1.5em',
              color: newReview.ratings >= star ? '#FFD700' : '#CCCCCC',
            }}
          >
            â˜…
          </span>
        ))}
      </div>

      <button onClick={handleCreateReview}>Submit Review</button>
    </div>
  );
}

export default ReviewsPage;
