import React, { useEffect, useState } from 'react';
import Donut from './components/Donut';
import axios from 'axios';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';

function App() {
    const [donuts, setDonuts] = useState([]);

    useEffect(() => {
        axios.get('http://localhost/projects/project3/backend/donuts.php').then((response) => {
            setDonuts(response.data);
        });
    }, []);

    return (
        <div>
            <h1>Donut Shop</h1>
            <div className="donuts">
                {donuts.map((donut) => (
                    <Donut key={donut.ID} name={donut.Name} description={donut.Description} price={donut.Price} />
                ))}
            </div>
        </div>
    );

    return (
        <Router>
            <Routes>
                <Route path="/" element={<Home />} />
                <Route path="/admin" element={<Admin />} />
            </Routes>
        </Router>
    );

    function Home() {
        return (
            <div>
                <h1>Welcome to Donut Shop</h1>
                <Donut />
            </div>
        );
    }
}

export default App;
