import React, { useState, useEffect } from 'react';
import axios from 'axios';

function Admin() {
    const [donuts, setDonuts] = useState([]);
    const [form, setForm] = useState({ name: '', description: '', price: '' });

    useEffect(() => {
        axios.get('http://localhost/projects/project3/backend/donuts.php').then((response) => {
            setDonuts(response.data);
        });
    }, []);

    const handleSubmit = () => {
        axios.post('http://localhost/projects/project3/backend/donuts.php', form).then(() => {
            setForm({ name: '', description: '', price: '' });
        });
    };

    return (
        <div>
            <h2>Admin Page</h2>
            <form onSubmit={handleSubmit}>
                <input type="text" placeholder="Name" value={form.name} onChange={(e) => setForm({ ...form, name: e.target.value })} />
                <input type="text" placeholder="Description" value={form.description} onChange={(e) => setForm({ ...form, description: e.target.value })} />
                <input type="text" placeholder="Price" value={form.price} onChange={(e) => setForm({ ...form, price: e.target.value })} />
                <button type="submit">Add Donut</button>
            </form>
        </div>
    );
}

export default Admin;
