import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Admin.css';

ReactDOM.render(<Admin />, document.getElementById('admin-root'));

function Admin() {
    const [donuts, setDonuts] = useState([]);
    const [form, setForm] = useState({ id: '', name: '', description: '', price: '' });
    const [isEditing, setIsEditing] = useState(false);

    useEffect(() => {
        fetchDonuts();
    }, []);

    // Fetch current donuts from the backend
    const fetchDonuts = () => {
        axios
            .get('http://localhost/projects/project3/backend/donuts.php')
            .then((response) => {
                setDonuts(response.data);
            })
            .catch((error) => {
                console.error('Error fetching donuts:', error);
            });
    };

    // Handle form submission for creating or updating donuts
    const handleSubmit = (e) => {
        e.preventDefault();
        if (isEditing) {
            axios
                .put('http://localhost/projects/project3/backend/donuts.php', form)
                .then(() => {
                    fetchDonuts();
                    setForm({ id: '', name: '', description: '', price: '' });
                    setIsEditing(false);
                })
                .catch((error) => console.error('Error updating donut:', error));
        } else {
            axios
                .post('http://localhost/projects/project3/backend/donuts.php', form)
                .then(() => {
                    fetchDonuts();
                    setForm({ id: '', name: '', description: '', price: '' });
                })
                .catch((error) => console.error('Error adding donut:', error));
        }
    };

    // Handle deleting a donut
    const handleDelete = (id) => {
        axios
            .delete(`http://localhost/projects/project3/backend/donuts.php?id=${id}`)
            .then(() => fetchDonuts())
            .catch((error) => console.error('Error deleting donut:', error));
    };

    // Handle editing a donut
    const handleEdit = (donut) => {
        setForm({
            id: donut.ID,
            name: donut.Name,
            description: donut.Description,
            price: donut.Price,
        });
        setIsEditing(true);
    };

    return (
        <div className="admin-container">
            <h2>Admin Dashboard</h2>
            <form className="donut-form" onSubmit={handleSubmit}>
                <h3>{isEditing ? 'Edit Donut' : 'Add New Donut'}</h3>
                <input
                    type="text"
                    placeholder="Name"
                    value={form.name}
                    onChange={(e) => setForm({ ...form, name: e.target.value })}
                    required
                />
                <textarea
                    placeholder="Description"
                    value={form.description}
                    onChange={(e) => setForm({ ...form, description: e.target.value })}
                    required
                ></textarea>
                <input
                    type="text"
                    placeholder="Price"
                    value={form.price}
                    onChange={(e) => setForm({ ...form, price: e.target.value })}
                    required
                />
                <button type="submit">{isEditing ? 'Update Donut' : 'Add Donut'}</button>
                {isEditing && (
                    <button
                        type="button"
                        className="cancel-btn"
                        onClick={() => {
                            setForm({ id: '', name: '', description: '', price: '' });
                            setIsEditing(false);
                        }}
                    >
                        Cancel
                    </button>
                )}
            </form>
            <h3>Current Donuts</h3>
            <table className="donuts-table">
                <thead>
                    <tr>
                        <th>Name</th>
                        <th>Description</th>
                        <th>Price</th>
                        <th>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {donuts.map((donut) => (
                        <tr key={donut.ID}>
                            <td>{donut.Name}</td>
                            <td>{donut.Description}</td>
                            <td>${donut.Price}</td>
                            <td>
                                <button className="edit-btn" onClick={() => handleEdit(donut)}>Edit</button>
                                <button className="delete-btn" onClick={() => handleDelete(donut.ID)}>Delete</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
}

export default Admin;
