import React, { useEffect, useState } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Admin from './components/Admin';
import axios from 'axios';
import './App.css';

function App() {
    return (
        <Router>
            <div>
                <header>
                    <h1>Donut Shop</h1>
                </header>
                <Routes>
                    <Route path="/" element={<Home />} />
                    <Route path="/admin" element={<Admin />} />
                </Routes>
                <footer>
                    <p>&copy; 2024 Donut Shop. All rights reserved.</p>
                </footer>
            </div>
        </Router>
    );
}

function Home() {
    const [donuts, setDonuts] = useState([]);

    useEffect(() => {
        axios
            .get('http://localhost/projects/project3/backend/api/donuts.php')
            .then((response) => {
                setDonuts(response.data);
            })
            .catch((error) => {
                console.error('Error fetching donuts:', error);
            });
    }, []);

    return (
        <div className="home">
            <div className="hero">
                <h1>Welcome to the Donut Shop</h1>
                <p>Indulge in the finest donuts crafted with love and perfection.</p>
                <button className="order-now-btn">Order Now</button>
            </div>
            <div className="donuts-container">
                <h2>Our Donuts</h2>
                <div className="donuts-grid">
                    {donuts.map((donut) => (
                        <div key={donut.ID} className="donut-card">
                            <h3>{donut.Name}</h3>
                            <p>{donut.Description}</p>
                            <p className="price">${donut.Price}</p>
                        </div>
                    ))}
                </div>
            </div>
        </div>
    );
}

export default App;
