import React, { useEffect, useState } from 'react';
import Admin from './components/Admin';
import './components/App.css';
import axios from 'axios';

function App() {
    const isAdminPage = window.location.pathname.includes('admin.html');

    return (
        <div>
            <header>
                <h1>Donut Shop</h1>
            </header>
            {isAdminPage ? <Admin /> : <Home />}
            <footer>
                <p>&copy; 2024 Donut Shop. All rights reserved.</p>
            </footer>
        </div>
    );
}

function Home() {
    const [donuts, setDonuts] = useState([]);

    useEffect(() => {
        axios
            .get('http://localhost/projects/project3/backend/donuts.php')
            .then((response) => {
                setDonuts(response.data);
            })
            .catch((error) => {
                console.error('Error fetching donuts:', error);
            });
    }, []);

    return (
        <div className="home">
            <div className="hero">
                <h1>Welcome to the Donut Shop</h1>
                <p>Indulge in the finest donuts crafted with love and perfection.</p>
                <button className="order-now-btn">Order Now</button>
            </div>
            <div className="donuts-container">
                <h2>Our Donuts</h2>
                <div className="donuts-grid">
                    {donuts.map((donut) => (
                        <div key={donut.ID} className="donut-card">
                            <h3>{donut.Name}</h3>
                            <p>{donut.Description}</p>
                            <p className="price">${donut.Price}</p>
                        </div>
                    ))}
                </div>
            </div>
        </div>
    );
}

export default App;
